document.addEventListener("contextmenu",e=>e.preventDefault());document.addEventListener("keydown",e=>{if(e.ctrlKey||e.metaKey||e.altKey||[112,123].includes(e.keyCode))e.preventDefault()});document.addEventListener("mousedown",e=>{if(!["PRE","CODE","KBD","I"].includes(e.target.tagName)||e.target.getAttribute("rel")!=="pre")e.preventDefault()});document.addEventListener("dragstart",e=>{if(!["PRE","CODE","KBD","I"].includes(e.target.tagName)||e.target.getAttribute("rel")!=="pre")e.preventDefault()});